import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const LoginPage = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Check the username and password with a function
    if (checkCredentials(username, password)) {
      navigate("/dashboard");
    } else {
      alert("Invalid username or password");
    }
  };

  const checkCredentials = (username, password) => {
    // Replace this with your own logic to check credentials
    return username === "admin" && password === "password";
  };

  return (
    <body>
      <div class="container">
        <div class="part">
          <h2>Temperature</h2>
          <label for="temp-lower">Lower Limit:</label>
          <input type="number" id="temp-lower" />
          <label for="temp-upper">Upper Limit:</label>
          <input type="number" id="temp-upper" />
        </div>
        <div class="part">
          <h2>Humidity</h2>
          <label for="humidity-lower">Lower Limit:</label>
          <input type="number" id="humidity-lower" />
          <label for="humidity-upper">Upper Limit:</label>
          <input type="number" id="humidity-upper" />
        </div>
        <div class="part">
          <h2>CO2</h2>
          <label for="co2-lower">Lower Limit:</label>
          <input type="number" id="co2-lower" />
          <label for="co2-upper">Upper Limit:</label>
          <input type="number" id="co2-upper" />
        </div>
      </div>
    </body>
  );
};

export default LoginPage;
