import React, { useState } from "react";
import "./Login.css";
import { Link, Navigate } from "react-router-dom";

export default function Login() {
  const [Username, setUserName, getUserName] = useState();
  const [Password, setPassword, getPassword] = useState();

  function CheckLogin(Username, Password) {
    alert();
    if (Username === "Ben Dover" && Password === "lmao") {
      Navigate("/TemperatureComponent");
    } else {
      Navigate("/login");
    }
  }

  return (
    <div className="login-wrapper">
      <h1>Please Log In</h1>
      <form>
        <label>
          <p>Username</p>
          <input type="text" onChange={(e) => setUserName(e.target.value)} />
        </label>
        <label>
          <p>Password</p>
          <input
            type="password"
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <div>
          <button type="submit" onClick={CheckLogin(Username, Password)}>
            Submit
          </button>
        </div>
      </form>
    </div>
  );
}
