import React, { useState } from "react";
import "./Login.css";
import { Link, Navigate } from "react-router-dom";
import TemperatureComponent from "./TemperatureComponent";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Check the username and password with a function
    if (checkCredentials(username, password)) {
      setIsLoggedIn(true);
    } else {
      alert("Invalid username or password");
    }
  };

  const checkCredentials = (username, password) => {
    // Replace this with your own logic to check credentials
    return username === "Ben Dover" && password === "lmao";
  };

  if (isLoggedIn) {
    Navigate("/TemperatureComponent");
  }

  return (
    <form onSubmit={handleSubmit} className="login-wrapper">
      <label>
        Username:
        <input type="text" value={username} onChange={handleUsernameChange} />
      </label>
      <br />
      <label>
        Password:
        <input
          type="password"
          value={password}
          onChange={handlePasswordChange}
        />
      </label>
      <br />
      <button type="submit">Log In</button>
    </form>
  );
};
export default Login;
